import { ethers } from 'ethers';
import { useEffect, useState } from 'react';
import contract from './Contract';

function App() {
  const [hasTicket, setHasTicket] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkTicketOwnership = async () => {
      try {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        const account = accounts[0];
        const balance = await contract.balanceOf(account, 1);

        setHasTicket(balance.toNumber() > 0);
      } catch (error) {
        console.error('Failed to check ticket ownership', error);
      } finally {
        setLoading(false);
      }
    };

    if (window.ethereum && contract) {
      checkTicketOwnership();
    }
  }, []);

  const renderContent = () => {
    if (loading) {
      return <p>Loading...</p>;
    }

    if (hasTicket) {
      return <p>Anda berhak masuk</p>;
    }

    return <p>Gagal</p>;
  };

  return (
    <div className="App">
      <h1>Validasi Kepemilikan NFT</h1>
      {renderContent()}
    </div>
  );
}

export default App;
