import React, { useEffect, useState } from 'react';
import Web3 from 'web3';

const CONTRACT_ADDRESS = '0x88bdEB822Fa9c8D8341dECA25c95072a72F48701'; // Replace with the actual contract address

const App = () => {
  const [contract, setContract] = useState(null);
  const [account, setAccount] = useState(null);
  const [isOwner, setIsOwner] = useState(false);

  useEffect(() => {
    loadBlockchainData();
  }, []);

  const loadBlockchainData = async () => {
    try {
      if (window.ethereum) {
        window.web3 = new Web3(window.ethereum);
        await window.ethereum.enable();
        const web3 = window.web3;
        const accounts = await web3.eth.getAccounts();
        setAccount(accounts[0]);

        const contractInstance = new web3.eth.Contract(
          [
            // Contract ABI
          ],
          CONTRACT_ADDRESS
        );
        setContract(contractInstance);
      } else {
        window.alert('Please install Metamask to use this DApp.');
      }
    } catch (error) {
      console.log(error);
      window.alert('Failed to load blockchain data.');
    }
  };

  const validateOwnership = async () => {
    if (contract && account) {
      try {
        const balance = await contract.methods.balanceOf(account, 1).call();
        setIsOwner(balance > 0);
      } catch (error) {
        console.log(error);
        window.alert('Failed to validate ownership.');
      }
    }
  };

  const handleValidation = async () => {
    validateOwnership();
  };

  return (
    <div>
      <button onClick={handleValidation}>Validate Ownership</button>
      {isOwner ? <p>NFT Terdeteksi, silakan masuk</p> : <p>Gagal</p>}
    </div>
  );
};

export default App;
